import Axios from "axios";
import React, { useEffect, useState } from "react";
import {Link} from 'react-router-dom';

const ProductList = () => {
    const [products, setProducts] = useState([]);

    const getProducts =()=>{
        /*let config = {
            headers: {
              authorization: `bearer ${JSON.parse(localStorage.getItem('token'))}`
            }
          }*/
          
        Axios.get("http://localhost:5000/api/product-list/").then((response) => {
            setProducts(response.data);
        })
    }

    const deleteProduct= (id)=>{
        //console.log(id);
        Axios.delete(`http://localhost:5000/api/delete-prod/${id}`).then((response) => {
            //console.log(response);
            getProducts();
        })
    }

    useEffect(() => {
        getProducts();
    },[]);

    const searchHandle = (event) => {
        let key = event.target.value;
        if(key) {
            Axios.get(`http://localhost:5000/api/search/${key}`).then((response) => {
                console.log(response);
                if (response) {
                    setProducts(response.data);
                }
            })
        }
    }

    //console.log(products);

    return(
        <div className="product-list">
            <h3>Product List</h3>
            <input type = "" className = 'search-product-box' placeholder = 'Search Product' onChange={searchHandle} />
            <ul className = "product-header">
                <li>ID Number</li>
                <li>Product Name</li>
                <li>Price</li>
                <li>Username</li>
                <li>Brand/Company</li>
                <li>Category</li>
                <li>Operation</li>
            </ul>
            {
                products.map((item, index) => 
                    <ul key={item.product_id}>
                        <li>{item.product_id}</li>
                        <li>{item.name}</li>
                        <li>{item.price}</li>
                        <li>{item.username}</li>
                        <li>{item.brand}</li>
                        <li>{item.category}</li>
                        <li><button onClick={()=>{deleteProduct(item.product_id)}}>Delete</button>
                        <Link to = {"/update/"+item.product_id}>Update</Link>
                        </li>
                    </ul>
                )
            }
        </div>
    )
};

export default ProductList;
