import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import logo from '../images/money_logo.png';

const Nav=()=>{
    const auth = localStorage.getItem('user');
    const navigate = useNavigate(); //Ensures nav bar always updated properly, checks whenever navigate is called even by other components.
    const logout = ()=>{
        localStorage.clear();
        navigate('/signup')
    }

    return(
        <div className = "navbar navbar-expand-md">
            <img src = {logo} className = "logo" />
            <button className="navbar-toggler navbar-dark" type="button" data-toggle="collapse" data-target="#main-navigation">
		        <span className="navbar-toggler-icon"></span>
	        </button>
            <div className='collapse navbar-collapse' id='main-navigation'>
                <ul className='navbar-nav'>
                    {auth ? <>
                        <li className='nav-item'><Link to="/">Products</Link></li>
                        <li><Link to="/add">Add Products</Link></li>
                        {/*<li><Link to="/update/:id">Update Products</Link></li>*/}
                        <li><Link to="/profile">Profile</Link></li>
                        <li><Link onClick={logout} to="/signup">Log Out ({JSON.parse(auth).name})</Link></li></> : <>
                        <li><Link to="/signup">Sign Up</Link></li>
                        <li><Link to = "/login">Log In</Link></li></>}
                </ul>
            </div>
        </div>
    )
}

export default Nav;
