import React, {useState} from 'react';

const LogIn = ()=> {
    const [username, setUsername] = useState('');
    const [password,setPassword] = useState('');
    
    useEffect(() => {
        const auth = localStorage.getItem('user');

        if(auth)
            {navigate('/');}
    },[]);
    
    const handleLogin = ()=>{
        Axios.post("http://localhost:5000/api/login/", {username: username, password: password}).then((response) => {
            localStorage.setItem("user", JSON.stringify(response.data));
            navigate('/');
        })
    };

    return(
        <div className='register'>
            <h1>Log-in</h1>
            <input className = "inputBox" type = "text" placeholder='Enter Username' onChange={(e)=>setUsername(e.target.value)} value = {username}></input>
            <input className = "inputBox" type = "password" placeholder='Enter Password' onChange={(e)=>setPassword(e.target.value)} value = {password}></input>
            <button className="appButton" type="button" onClick={handleLogin}>Log-In</button>
        </div>
    );
};

export default LogIn;
